import { render } from '@testing-library/react';
import React from 'react';

class Suggestion extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "mon@email.com",
            email: "monPassw0rd",
            notes: "",
            extras: "James"
        }
        this.updateName = this.updateName.bind(this);
        this.updateEmail = this.updateEmail.bind(this);
        this.updateNotes = this.updateNotes.bind(this);
        this.updateExtras = this.updateExtras.bind(this);
    }

    // changeName(e) {
    //     this.setState({ name: e.target.value });
    // }


    updateName(event) {
        this.setState({ name: event.target.value });
    }

    updateEmail(event) {
        this.setState({ email: event.target.value });
    }

    updateNotes(event) {
        this.setState({ notes: event.target.value });
    }

    updateExtras(event) {
        this.setState({ extras: event.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        fetch("/auth/signup", {
            method: "POST",
            headers: new Headers({
                "Content-Type": "application/json",
            }),
            body: JSON.stringify({
                email: email,
                password: password,
                name: name,
                lastname: lastname,
            }),
        })
            .then((res) => res.json())
            .then(
                (res) => setFlash(res.flash),
                (err) => setFlash(err.flash)
            );
    };

    render() {
        return (
            <div className="Suggestion">
                <h1>{JSON.stringify(this.state, 1, 1)}</h1>
                <form onSubmit={this.handleSubmit}></form>
                <form onSubmit={this.handleSubmit}>
                    <input type="name" name="name" onChange={this.updateName} />
                    <input type="email" name="email" onChange={this.updateEmail} />
                    <input type="notes" name="notes" onChange={this.updateNotes} />
                    <input type="message" name="message" onChange={this.updateExtras} />
                    <input type="submit" value="Submit" />
                </form>






                {/* <h1>{this.state.name}</h1>
                <form>
                    <label for="name">Name:</label>
                    <input type="text" id="name" name="name" onChange={this.changeName}></input>

                    <label for="email">Email address:</label>
                    <input type="text" id="email" name="email"></input>

                    <label for="notes">Notes:</label>
                    <input type="text" id="notes" name="notes"></input>

                    <label for="message">Message:</label>
                    <input type="text" id="message" name="message"></input>
                </form> */}
            </div>
        )
    }

}

export default Suggestion;
