import { render } from '@testing-library/react';
import React from 'react';

class Suggestion extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "mon@email.com",
            email: "monPassw0rd",
            notes: "",
            extras: "James"
        }
        this.updateName = this.updateName.bind(this);
        this.updateEmail = this.updateEmail.bind(this);
        this.updateNotes = this.updateNotes.bind(this);
        this.updateExtras = this.updateExtras.bind(this);
    }

    // changeName(e) {
    //     this.setState({ name: e.target.value });
    // }


    updateName(event) {
        this.setState({ name: event.target.value });
    }

    updateEmail(event) {
        this.setState({ email: event.target.value });
    }

    updateNotes(event) {
        this.setState({ notes: event.target.value });
    }

    updateExtras(event) {
        this.setState({ extras: event.target.value });
    }

    handleSubmit = (e) => {
        // if (this.state.password === this.state.passwordCheck) {
        //     e.preventDefault();
        //     let obj = JSON.stringify(this.state, 1, 1);
        //     console.log(obj);
        // } else {
        //     alert("password check does not match!")
        // }
    }

    render() {
        return (
            <div className="Suggestion">
                <h1>{JSON.stringify(this.state, 1, 1)}</h1>
                <form onSubmit={this.handleSubmit}></form>
                <form onSubmit={this.handleSubmit}>
                    <input type="email" name="email" onChange={this.updateName} />
                    <input type="text" name="password" onChange={this.updateEmail} />
                    <input type="text" name="passwordCheck" onChange={this.updateNotes} />
                    <input type="text" name="firstName" onChange={this.updateExtras} />
                    <input type="submit" value="Submit" />
                </form>






                {/* <h1>{this.state.name}</h1>
